UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично! Я так рад это слышать.\n\nКак мне не терпится вернуться в мою башню и поработать над прорисовками... Я так завидую вам! Побывать в Азшаре... думаю, это мечта всех изучающих чародейские науки.\n\nЛадно, хотя мы и не говорили о награде, поскольку вы работаете на лигу Исследователей, но вы ее заслуживаете.', `RequestItemsText_loc8`='Вы вернулись! Замечательно. А готовальню мою принесли?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=3461;
