UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы только взгляните на это! Мой дневник! Я думал, он утерян навсегда, как в все, что нам пришлось бросить в базовом лагере. Спасибо, что вернул мне его!', `RequestItemsText_loc8`='Приветствую! Мы с коллегами рады видеть собрата по разуму после стольких недель общения с диким зверьем! Что привело тебя в Кратер Ун\'Горо?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=3884;
