UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично! Детеныши из этих яиц помогут мне осуществить задуманное! Возьмите это в знак моей признательности, пусть вам всегда везет на любой охоте, |3-6($C).', `RequestItemsText_loc8`='$N, вы набрали для меня яиц виверн? Мне не терпится начать дрессировку моих новых ветрокрылов!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=4767;
