UPDATE `locales_quest` SET `OfferRewardText_loc8`='Рад встрече, |3-6($C). В последнее время я посвятил большую часть времени изучению Некроситета, и, кажется, знаю способ, как добыть ключ... который отомкнет входную дверь.$B$BПредставляю, какие кошмары таятся за ней, $N. Не понимаю, как герои вроде тебя решаются на такие подвиги...', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=5533;
