UPDATE `locales_quest` SET `OfferRewardText_loc8`='\"Да будет так, отец Кенарий. Да будет так.\"$B$BДендрит делает быстрый незаметный жест в воздухе у себя над головой. Его окутывает тонкая аура силы.$B$B\"Первое обличье, которое тебе следует освоить, - это обличье медведя. Я направлю тебя на путь понимания этого аспекта бытия друида, но уж усвоить знания и усовершенствоваться на этом пути - это твоя забота! И так отныне будет всегда.\"', `RequestItemsText_loc8`='Вы прибыли из Тельдрассила? Скажите, как там ловится рыба?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=5921;
