UPDATE `locales_quest` SET `OfferRewardText_loc8`='О, чудесно! Какое отличное мясо! Оно ведь от Зарга, да? Этот $r<орк> знает, как завоевать женское сердце…\n\nОх, мне не терпится его приготовить. Но не слишком много! Мясо вкуснее, когда его подают редко, верно?', `RequestItemsText_loc8`='Какой вкусный запах! Ты часом не сырое мясо несешь?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=6384;
