UPDATE `locales_quest` SET `OfferRewardText_loc8`=NULL, `RequestItemsText_loc8`='Прямо из Кабестана, свежие лезвия для пил Песни Войны! Да, они у меня с собой.$B$BНо чем вы мне заплатите? Вы же не можете забрать их просто так! Мое время дорого стоит!$B$BЧестно говоря, мне нужно доброе ружье для моих поездок. Девушке ведь надо уметь защитить себя, вы ведь понимаете? Есть одно ружье, которое мне по нраву, и я уверена, что инженер без проблем такое сделает.$B$BИ если вы принесете мне хороший мушкетон, я отдам вам партию лезвий. Идет?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=6581;
