UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хо-хо-хо! Я Дедушка Зима, и я явился к вам благодаря добросердечию Пастбищ Дымного Леса! \"Свежие фрукты от нашего села да к вашему столу, вкусная и здоровая пища из Пастбищ Дымного Леса!\" Желаю вам чудесного Зимнего Покрова!\n\nВсе, кто хорошо себя вел в этом году, получат лакомства от Пастбищ Дымного Леса в качестве подарка на праздник Зимнего Покрова. Ур-р-ра!', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=7024;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ну, ты и угодил Дедушке Зиме, $N! Доброго тебе праздника Зимнего Покрова.\n\nА вот тебе кое-что... ну, за труды, ты понимаешь.', `RequestItemsText_loc8`='От всех этих разговоров об угощении у меня самого аппетит разыгрался. Мммм... Прянички с молочком...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=7025;
