UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ха-ха-ха! Я так и знала, что ты вернешься.$B$BИва бормочет себе под нос.$B$B... Как легко убедить.$B$B… пойдет на что угодно за сходную цену. Хе-хе-хе...', `RequestItemsText_loc8`='Не задавай лишних вопросов!$B$BЗаказ есть заказ.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=7028;
