UPDATE `locales_quest` SET `OfferRewardText_loc8`='О, отлично! Эти припасы помогут накормить всех моих сородичей, собравшихся сюда на праздник!\n\nСпасибо тебе, $N Я у тебя в долгу. Прошу, возьми эти монеты... и последуй моему совету – пей сегодня от всей души и присоединяйся к праздничным танцам!', `RequestItemsText_loc8`='Добрый день, |3-6($C! Ты хочешь отпраздновать Новый Год у нас, в Громовом Утесе? Или просто дать отдых усталым копытам, прежде чем снова отправляться на охоту?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=8861;
