UPDATE `locales_quest` SET `OfferRewardText_loc8`='Значит, Лимель просит тебя отправиться в Луносвет и доставить ее товар? Долетим во мгновение ока… за скромную плату, естественно.', `RequestItemsText_loc8`='Да, что тут у тебя?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9130;
