UPDATE `locales_quest` SET `OfferRewardText_loc8`='Плеть познала силу нашего гнева, $N! Скоро их проклятая цитадель превратится в руины!\n\nТеперь, когда их силы ослабли, пора ударить и по другим стратегическим точкам.', `RequestItemsText_loc8`='Ну, дело сделано? Дар\'Кхан убит?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9220;
