UPDATE `locales_quest` SET `OfferRewardText_loc8`='Итак, свершилось. Подлый предатель получил по заслугам.\n\nТак этот подвиг совершила ты? Подобные свершения доказывают, что ваша раса не ослабела, $N.\n\nЯ вижу, что вести Лортемара значительно улучшат его отношения с Траллом.', `RequestItemsText_loc8`='А, гость из Кель\'Таласа! Какие вести ты принесла?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9621;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Видения, говоришь? Интересно... Так пророчество сбылось?$B$BЗначит, они видели энергетическое ядро Экзодара? И О\'рос тоже являлся им в видении? Что бы это значило?', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9622;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тораллиус вздыхает. $B$BЛадно, давай распоряжения – почитаем.', `RequestItemsText_loc8`=' Тораллиус поправляет повязку на глазу и пристально изучает вас. $B$BНеужели мы не можем послать на помощь Длани Аргуса достойного героя?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9623;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Нелегко собирать груши, да? У меня на это уходит все больше и больше времени…$B$BНаверное, скоро мне и вовсе придется отказаться от грушевых пирогов. Даже не представляю, как я тогда буду жить. Но пока что, благодаря тебе, $N, мне хватит груш, чтобы испечь один-два пирога...', `RequestItemsText_loc8`='Найти песчаные груши все труднее – а это значит, что я могу печь все меньше пирогов, а уж элеккам и вовсе почти ничего не останется... Но, может, ты принесешь мне хоть немного груш?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9624;
