UPDATE `locales_quest` SET `OfferRewardText_loc8`='Послушай, $N, мне необходима твоя помощь. У меня есть план, благодаря которому мы сумеем разгромить армию Солнечных Ястребов, однако для его осуществления понадобится дополнительная информация.$B$BНам необходимо найти слабое место в их обороне. К сожалению, никому еще не удалось подобраться достаточно близко к Векторной Спирали, чтобы это выяснить. Если нам удастся заставить пленника говорить, возможно, мы сумеем прорвать оборону и разрушить Спираль.$B$BТы понимаешь, о чем я, не так ли? Я хочу, чтобы проклятая Спираль была взорвана!', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9753;
