UPDATE `locales_quest` SET `OfferRewardText_loc8`='Эти острова станут домом для тысяч и тысяч; многие останутся здесь на всю жизнь. Если таково твое желание, можешь присоединиться к ним – но ты ведь вовсе не этого хочешь, $N?$B$B Велен кивает. $B$BДа, ты многое свершишь, прежде чем покинешь этот мир. Не страшись своей судьбы, шагни ей навстречу!$B$BНа прощание прими от меня два дара: плащ и перстень. Такие плащи носят только достойнейшие из братства Длани Аргуса, а права носить эти перстни-печатки удостаиваются лишь величайшие мудрецы. Ты $Gдостоин:достойна; этих даров – носи их с гордостью.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=9762;
