UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хорошая работа, $N. Благодаря уничтожению подавляющего поля мне будет гораздо легче подготовить магию, которая наконец-то снимет проклятье с башни.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10176;
