UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я должен был догадаться, что это он отправил тебя за мной. Он пытался отговорить меня от возвращения в Сетеккские залы, но разве я смогу жить спокойно, если позволю Лакке погибнуть там?', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10180;
