UPDATE `locales_quest` SET `OfferRewardText_loc8`='Это было доброе дело, $N. Я больше не чувствую беспокойства, исходящего от духа Датрика. Я много бы отдал за возможность еще раз поговорить с ним, но достаточно и того, что он наконец-то нашел покой.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`='Упокойте дух Датрика', `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10182;
