UPDATE `locales_quest` SET `OfferRewardText_loc8`='Эй, смотри, куда направляешь эту штуку! Она выставлена на смертельное поражение!!!\n\nПохоже, тебе удалось как следует подзарядить батарейки. Ну что ж, теперь мои ребятки смогут двигаться.', `RequestItemsText_loc8`='Ну как, удалось зарядить батарейки в устройстве?', `ObjectiveText1_loc8`='Перезарядка батарей', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10190;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Получилось! Получилось! Я готова тебя расцеловать!\n\nОй... у Маккса А. Миллиона Mk. V опять кончилась энергия. Ну ладно, он сегодня хорошо поработал, а остальное неважно!\n\nКомандующий ракетами Фьюзеляж будет доволен – он сможет подключить эти дренейские машины к своей ракете Х-52! А я получу продвижение! Я так рада! Сейчас, я чем-нибудь тебя вознагражу.\n\nВот, выбирай из этого.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10191;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Изображение верховного мага Варгота словно бы изучает Компендиум.\n\nДа, книга в плохом состоянии, но если учесть, как с ней обращались, чудо еще, что она вообще сохранилась. Сбереги ее, $N. Без нее у нас мало надежды пробиться через стену, ограждающую башню.', `RequestItemsText_loc8`='Ты принес книгу?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10192;
