UPDATE `locales_quest` SET `OfferRewardText_loc8`='Просто прекрасно! Я, можно сказать, чуть ли не самолично почувствовал, как земля содрогнулась от взрыва!\n\nКак насчет еще одной подрывной работенки?', `RequestItemsText_loc8`='Ну как Легион, обрадовался подарочку, который мы для него подготовили, а, $N', `ObjectiveText1_loc8`='Выведите из строя Портал Зилус', `ObjectiveText2_loc8`='Выведите из строя Портал Круул', `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10208;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты не можешь сказать – искажается ли изображение или верховный маг действительно улыбается.\n\nТеперь, когда мне возвращено все, что было отобрано, у меня есть все необходимое, дабы пробиться сквозь магию Кель\'таса. Я немедленно начинаю работать над заклинанием.', `RequestItemsText_loc8`='Удалось ли тебе добыть камень Студенора?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10209;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Когда вы приблизились к А\'далу, вас пронизал внутренний свет, а когда вы обратились к нему, свет усилился и в голове зазвучала нежная мелодия.\n\nЗдесь вы в безопасности.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10210;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Надеюсь, вы узнали кое-что о Шаттрате, $N. Понадобится время, чтобы информация устоялась в вашей памяти.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10211;
