UPDATE `locales_quest` SET `OfferRewardText_loc8`='Неплохая работа, $N. Подожди минуту, я прогляжу это описание. \n\nЖречество благодарно тебе, $C.', `RequestItemsText_loc8`='Давай посмотрим, что это такое, $N.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10245;
