UPDATE `locales_quest` SET `OfferRewardText_loc8`='Уры! Ты знаешь, как трудно раздобыть эти штуки? Тут у нас не существует регулярной поставки запчастей.\n\nВот что я скажу – в качестве платы можешь взять что-нибудь из моей коробки с хламом. Я уверен, там ты найдешь для себя что-нибудь полезное. А если нет, то продай.\n\nНу ладно, давай, я заберу у тебя эту штуку и встрою в свой спектральный анализатор Длинношеего прежде, чем неисправный сервопривод взорвется!', `RequestItemsText_loc8`='Надеюсь, ты здесь из-за того объявления о розыске, которое я вывесил!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10261;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Эти знаки отличия – хорошее начало.\n\nК несчастью, за время твоего пребывания в Груде еще один мой агент в Заксис сообщил мне, что астральный налетчик Несаад все еще жив!', `RequestItemsText_loc8`='Дело сделано? Удалось ли тебе стереть с лица Запределья этих негодяев из братства Заксис и достать их знаки отличия?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10262;
