UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тебе удалось совершить невозможное, $N. К сожалению, юный вождь этого не запомнит. В его памяти должны остаться только те события, которые случились бы, не будь искажения линии времени... Андорму ждет тебя в логове нашего господина.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`='Предназначение Тралла исполнено', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10284;
