UPDATE `locales_quest` SET `OfferRewardText_loc8`='Виера Шепот Солнца? Эта маленькая дрянь?\n\nНу я ей покажу, как переходить мне дорогу!', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10287;
