UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты спасла работу всей моей жизни, $N.\n\nМеня называют безумцем и смеются надо мной, говорят, что я одержим Пустынной Грядой и Кривыми Полями. Теперь, с этим осколком души, я открою все секреты демонов Бездны. Силы Бездны будут повиноваться мне!', `RequestItemsText_loc8`='Ты убила Барона Бездны Галаксиса? Где осколок его души?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10295;
