UPDATE `locales_quest` SET `OfferRewardText_loc8`='Равандвир тщательно осматривает кристаллы, затем выбирает один.\n\nЭтот кристалл подойдет. Я изменю форму гнезда посоха, чтобы закрепить новое навершие.\n\nКогда верховный маг Варгот увидет это, он будет разочарован, но я переживу разочарование учителя, если это будет означать, что мы освободим его.', `RequestItemsText_loc8`='Удалось ли тебе найти кристаллы, пригодные для изготовления нового навершия к посоху верховного мага?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10300;
