UPDATE `locales_quest` SET `OfferRewardText_loc8`='Так быстро? Что ж, отлично. Надеюсь, скоро я придумаю, как справляться с этими мутантами, не убивая их.$b$bУ меня появилась пара идей, но без твоей помощи мне не обойтись.', `RequestItemsText_loc8`='Я скоро придумаю, что делать с этими тварями. Дай мне время.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10302;
