UPDATE `locales_quest` SET `OfferRewardText_loc8`='Комендант Смертехрон берет у тебя книгу и пролистывает страницы.\n\nХмм...\n\nПохоже, я был прав. Есть некая закономерность.\n\nСамые агрессивные и опасные призраки принадлежат самым сильным и высокоранговым магам Кирин-Тора, обитавшим в деревне. Обычные жители и торговцы в сравнении с ними почти не представлены. Что бы это могло значить?', `RequestItemsText_loc8`='Удалось ли тебе найти регистрационную книгу?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10312;
