UPDATE `locales_quest` SET `OfferRewardText_loc8`='Мердад кивает, затем изучает результаты диагностики.\n\nТревожные показатели. Ущерб гораздо сильнее, чем я считал. Я могу послать в Штормовую Вершину за инструментами для ремонта, но это бесполезно, если мы, в первую очередь, не защитим генератор от того, что так сильно повредило его.', `RequestItemsText_loc8`='Удалось ли тебе снять результаты диагностики?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10417;
