UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я не могу передать, какое большое дело вы совершили, $N. Вы спасли огромное количество наших собратьев от бесконечной тьмы. Я мало что могу сделать для живых и у меня нет ничего, что стало бы достойной наградой для вас. Возьмите сами то, что вам нужнее.', `RequestItemsText_loc8`='Вы спасли нас всех! Как нам отблагодарить вас?', `ObjectiveText1_loc8`='Убито мстительных дренеев', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10842;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Леорокс меряет вас проницательным взглядом.\n\nСмерть Гноша Брогната – большое благо для всех Мок\'Натал. И для тебя это благо.\n\nПосмотрим, не сможещи ли ты нам помочь в еще более важном деле.', `RequestItemsText_loc8`='Тчали не будет говорить, пока $N не использует фетиш и не проклянет дома и башни кланов Камнерогов и Кровавого Молота!!!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10843;
