UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хорошая подборка. Мы сумеем достойно использовать одного-двух.\n\nИ я, кстати, знаю, как это сделать!', `RequestItemsText_loc8`='Ну, побывал на Заставе Камнерогов и отобрал у них тотемы?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10851;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы нашли всех моих друзей! Теперь хоть будет, с кем поиграть. Спасибо, господин $N.\n\nНу, раз у меня есть, с кем играть, может, вы тогда возьмете мою игрушечку? Тоже поиграете, а?', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`='Детей спасено', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10852;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты сегодня сделал доброе дело, |3-6($C). Теперь освобожденные души драконов воистину воспоют. А та остаточная часть души, которую они оставили после себя – могучее лекарство.\n\nХочу спросить – а не собираешься ли ты на север? Там есть кое-что весьма любопытное для меня, связанное с деятельностью духов Астрала.\n\nЯ чувствую, оттуда будет много лиха.', `RequestItemsText_loc8`='Драконы Звенящего гребня – угроза для нас, но я думаю, что они величественные существа.\n\nИли, по крайней мере. были таковыми. Надеюсь, ты сумеешь спасти их души.\n\nИ если тебе понадобится еще тотемов. У меня найдется несколько штук.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10853;
