UPDATE `locales_quest` SET `OfferRewardText_loc8`='Да что ты говоришь! Разве возможно такое? Да что я говорю! Конечно, возможно, если уж вызыватель духов так говорит!\n\nПомнишь, я говорил, что нам еще может понадобиться твоя помощь? Похоже, этот час настал.', `RequestItemsText_loc8`='Удалось собрать необходимое количество световых сфер братства Разаани?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10865;
