UPDATE `locales_quest` SET `OfferRewardText_loc8`='Наконец-то мы избавились от этих пауков. Вы убили многих из них и даже саму их матку, но я слишком сильно отравлен, чтобы выбраться отсюда.\n\nПожалуйста, возьмите некоторые из моих вещей – это немного облегчит мою ношу и поможет вам добраться до заставы Ша\'тар.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=10921;
