UPDATE `locales_quest` SET `OfferRewardText_loc8`='Кончено, значит. Ты освободила нас от оков сынов Груула. Ты спасла клан Кровавого Молота от полного укничтожения.\n\nСловами не выразить, в каком долгу перед тобой ВСЕ огры Острогорья, как Кровавые Молоты, так и Камнероги, $N.\n\nСмотри вниз – уже теперь огры собрались воздать тебе почести. Пусть Груул и его марионетка, старый король Молгар, прячутся в его логове. Иди же к ограм вниз, как наша новая королева!', `RequestItemsText_loc8`='Не взяла еще душу Скуллока?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11000;
