UPDATE `locales_quest` SET `OfferRewardText_loc8`='Огромное спасибо за помощь! Пока дворфы Черного Железа не сложили оружие, а Фестиваль пива продолжается, мне не обойтись без помощников. Если ты хочешь еще подзаработать, приходи завтра.$b$bВпрочем, довольно болтать. Надо заплатить тебе за труды. Вот билеты, желаю веселого Фестиваля пива!', `RequestItemsText_loc8`='Мне нужны бочки! Чего ты ждешь?!', `ObjectiveText1_loc8`='Доставлено бочек', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11122;
