UPDATE `locales_quest` SET `OfferRewardText_loc8`='Инспектор смотрит на вас с нескрываемым недоверием.\n\nМне сказали, что ты будешь... помогать в расследовании. Что же, хорошо.\n\nЯ обработал тут все вот этим порошком, чтобы узнать, кто напал на гостиницу. Он поможет выявить улики, связанные с эти нападением. Обследуй все, что покажется подозрительным.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11124;
