UPDATE `locales_quest` SET `OfferRewardText_loc8`='Очень хорошо, $N. Думаю, нам удалось застать их врасплох. Похоже, эти агенты считали себя в безопасности за городскими стенами.$B$BСо временем подстрекатели вернутся и снова будут распространять дезинформацию. А мы пока воспользуемся тем временем, что ты для нас выиграл, чтобы отогнать их как можно дальше.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`='Выявлено дезертиров-подстрекателей', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11126;
