UPDATE `locales_quest` SET `OfferRewardText_loc8`='Крог рассматривает оружие, принесенное вами из деревни Черного Копыта.\n\nЯвно работа Мозарна. Дальнейшее расследование привлечет слишком много внимания, но теперь у нас развязаны руки для борьбы со Зловещим Тотемом.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11200;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Крог читает боевые планы.\n\nВ том, что Зловещий Тотем двинется на людей, сомнений нет. Но что он замышляет против нас? Действия этого клана давно вызывают беспокойство в рядах Орды, но вождю до сих пор удавалось рассеивать его.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11201;
