UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хорошо, что Табета прислала тебя. Неплохо было бы, если б она передала каких-то припасов, пока сюда не доставят груз с разбившегося дирижабля, но, как мне известно, сейчас ей просто нечем поделиться. Если у тебя есть вкус к приключениям, то я назначил награду за одного особенно противного ящера.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11211;
