UPDATE `locales_quest` SET `OfferRewardText_loc8`='Этот потрепанный свиток покрыт яркими рисунками. Вы чувствуете, что собаколов говорил вам именно о такой находке.', `RequestItemsText_loc8`='А, вот и ты!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11253;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Верховный палач внимательно рассматривает карту, изучая символы вокруг Фьорда.>\n\nТак, это восточное побережье. Значит, деревни Потрошителей Драконов и Снежескорнов находятся здесь и здесь... а это, должно быть, мы, приговоренные к смерти.', `RequestItemsText_loc8`='Ты хочешь мне что-то показать, |3-6($C)?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11254;
