UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Старый Ледоспин протягивает руку за трезубцем своего сына, и вы замечаете, что его пальцы слегка дрожат. Неужели этот дряхлый мурлок способен испытывать скорбь?>\n\n<Неужели вы видите скупые слезы в уголках его глаз – или это просто вода?>', `RequestItemsText_loc8`='<Старый Ледоспин внимательно окидывает вас мутным взглядом. Очевидно, он ожидает увидеть в ваших руках трезубец его сына.>', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11422;
