UPDATE `locales_quest` SET `OfferRewardText_loc8`='Да, мертвее не бывает… Странно, что нерубы не сожрали труп.\n\nПосле тщательного осмотра вы приходите к выводу, что это было самоубийство – Лютер отравился. Умно. К отравленному трупу нерубы на выстрел не подойдут.\n\nВ руке у Лютера зажат дневник...', `RequestItemsText_loc8`='Нужно уничтожить эти кладки!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11614;
