UPDATE `locales_quest` SET `OfferRewardText_loc8`='Смотритель и его подчиненные, должно быть, думали, что карьер станет для них легкой добычей, но ты показал им, как они ошибались, $N. Я глубоко благодарен тебе за помощь и уверен, что предки не лишат и впредь тебя своего благосклонного внимания.', `RequestItemsText_loc8`='Ты завершил ритуал возвращения предков?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11623;
