UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Ведунья Эйри принимает у вас тотем.>\n\nТак тотем подействовал, как надо? Чтобы его сделать, пришлось прибегнуть к древней магии, которую ведуны не использовали поколениями. До нас дошли только обрывки древних знаний...', `RequestItemsText_loc8`='Как... как ты нашел меня?', `ObjectiveText1_loc8`='Восточный котел очищен', `ObjectiveText2_loc8`='Центральный котел очищен', `ObjectiveText3_loc8`='Западный котел очищен', `ObjectiveText4_loc8`=NULL WHERE `Id`=11647;
