UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я сожалею о гибели этих бурь, но слово мое крепко... даже данное смертному существу вроде тебя.\n\nТеперь посмотрим, смогу ли я сделать что-то, что окажется тебе полезным.', `RequestItemsText_loc8`='Когда ты принесешь частицы бурь, я смешаю с ними часть своей сущности. В результате получится как раз то, что тебе надо.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11649;
