UPDATE `locales_quest` SET `OfferRewardText_loc8`='Прошу прощения, $N. Я знаю, что это была малоприятная работа.\n\nЕсли бы не то отчаянное положение, в каком мы оказались, я бы никогда не просил тебя помочь таким образом.\n\nИтак... что тебе удалось выяснить?', `RequestItemsText_loc8`='Ну что? Удалось тебе выяснить, где держат леди Эванор?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11663;
