UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Ведунья опускает голову и вздыхает с облегчением.>\n\nХорошее начало. Если бы это еще была единственная наша проблема…', `RequestItemsText_loc8`='Входи в любую пещеру, |3-6($C). Быстрее, используй семена, чтобы положить конец чуме!', `ObjectiveText1_loc8`='Чумной котел нейтрализован', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11677;
