UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ваши героические деяния не будут забыты, |3-6($C)!\n\nПрошу вас, примите это вместе с моей искренней благодарностью.', `RequestItemsText_loc8`='Ты смеешь приблизиться ко мне, смертное существо?\n\nРади твоих еще нерожденных детей – молись, чтобы твое дело стоило моего беспокойства.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11681;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='А, |3-6($R), вот и довелось нам встретиться снова!\n\nВпрочем, когда ты услышишь мои слова, ты пожалеешь об этом.', `RequestItemsText_loc8`='Ты смеешь приблизиться ко мне, смертное существо?\n\nРади твоих еще нерожденных детей – молись, чтобы твое дело стоило моего беспокойства.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11682;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Спасибо, $N.\n\nКороль-лич и его Плеть – нарыв на теле Нордскола. Нам нельзя останавливаться, пока они не умрут истинной смертью, а его существованию будет положен конец!', `RequestItemsText_loc8`='Вряд ли тебе удалось уничтожить так много созданий Плети за столь короткое время!', `ObjectiveText1_loc8`='Уничтожено созданий Плети в Талрамасе', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11683;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Великий Отец Махан берет у вас карту, но даже не смотрит на нее.>\n\nТвою информацию мы используем позже, когда будем готовы осуществить наш план.', `RequestItemsText_loc8`='Ну что, все воронки отмечены на карте?', `ObjectiveText1_loc8`='Отметить расположение южной воронки', `ObjectiveText2_loc8`='Отметить расположение северо-восточной воронки', `ObjectiveText3_loc8`='Отметить расположение северо-западной воронки', `ObjectiveText4_loc8`=NULL WHERE `Id`=11684;
