UPDATE `locales_quest` SET `OfferRewardText_loc8`='Все куда хуже, чем описано в дневнике Лютера! Порождения Плети здесь повсюду, они заражают все, к чему прикоснутся!\n\n<Икорис кивает.>\n\nДело становится все хуже и хуже, хотя, казалось бы, хуже некуда…', `RequestItemsText_loc8`='Мне нужно больше времени! Не подпускай этих свиней к моим кодо!', `ObjectiveText1_loc8`='Разведать Амбар Песни Войны', `ObjectiveText2_loc8`='Разведать ферму Торпа', `ObjectiveText3_loc8`='Разведать скотобойню Песни Войны', `ObjectiveText4_loc8`=NULL WHERE `Id`=11686;
