UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Нок фыркает.>\n\nПленные? Да какие это пленные! Это трусливые шавки! Ты только взгляни на них! Ты знаешь, что все они – все без исключения! – явились под стены нашей крепости и добровольно сложили оружие?\n\nНасколько мы поняли, если они попадают в плен, их потом разжалуют и отпускают по домам!\n\n<Нок плюется.>\n\nЖалкие твари!', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11709;
